// OMDB API Key
// 6480163c

const API_URL = 'https://www.omdbapi.com?apikey=6480163c';

const movie1 = {
    "Title": "No Time to Die: Official Title Reveal of Bond 25",
    "Year": "2019",
    "imdbID": "tt10839440",
    "Type": "movie",
    "Poster": "https://m.media-amazon.com/images/M/MV5BZjA3ZjhiODUtN2QxYy00MzlhLWFlYzYtZTg1ODQ2YjM0YjY3XkEyXkFqcGdeQXVyMzM3ODE4MzY@._V1_SX300.jpg"
}

const App = () => {
  const [movies, setMovies] = useState([]);
  const searchMovies = async(title) =>{
     const response = await fetch(`${API_URL}&s={title}`);
     const data = await response.json();
     console.log(data.Search);
  }
  useEffect(() => {
    searchMovies('Aquaman');
  }, []);


  return (
    <div className="app">

      <h1>MoxieLand</h1>

      <div className="search">
        <input placeholder="Search for movies" value="Spiderman" onChange={() =>{}}/>

        <img src={searchIcon} alt="search" onClick={() =>{}}/>
      </div>


      <div className="container">
        <MovieCard movie1={movie1}/>

      </div>


    </div>
  );
}

export default App
