import React, { useEffect, useState } from "react";
import Back from "../common/back/Back" ;
import searchIcon from "../../search.svg";
import "../../App.css";
import Card from '../../card';
import XtremePapers from '../../images/xtremepapers.jpeg'
import { Link } from "react-router-dom";
import sanityClient from "../../client";


const IGCSE = () => {
  const [allResources, setAllResources] = useState(null);
  useEffect(() => {
    sanityClient
      .fetch(
        `*[_type == "resource"]{
        name,
        syllabus,
        type,
        link,
        slug,
        image
        }
      }
    }`
      )
      .then((data) => setAllResources(data))
      .catch(console.error);
  }, []);

  return (
    <>
        <Back title="IGCSE"/>

        <div className="search">
        <input placeholder="Search for IGCSE resources" value=""/>

        <img 
        src={searchIcon} 
        alt="search"/>
        </div>
      <div className="wrapper">
        {allResources &&
          allResources.map((resource, index) => (
        <Link>
        < Card img={XtremePapers} title={resource.name} type={resource.type}/>
        </Link>
         ))}
      </div>
    </>

  )

}

export default IGCSE
