import React from 'react'
import Back from "../common/back/Back" ;
import { useForm, ValidationError } from '@formspree/react';
import { GoogleReCaptchaProvider, GoogleReCaptcha } from 'react-google-recaptcha-v3'
import "../../App.css"
import Thanks from "../thanks/thanks"


const Contact = () => {
  const [state, handleSubmit] = useForm("xknpnjdv");
  if (state.succeeded) {
    return <Thanks />;
  }
  function onFormSubmit(e) {
  e.preventDefault();
  window.grecaptcha.ready(function() {
    window.grecaptcha.execute('6LeeDGogAAAAAI1leH_NawmoXNo-L_yBoi5O1qwz', {action: 'submit'}).then(function(token) {
      // Send form value as well as token to the server
    });
  });
}

  return (
    <>
      <Back title="Contact"/>
      <div className="form">
        <form onSubmit={handleSubmit}>
        <input
          id="email"
          type="email" 
          name="email"
          placeholder="Please enter your email address" required
        />
        <ValidationError 
          prefix="Email" 
          field="email"
          errors={state.errors}
        />
        <textarea
          id="message"
          name="message"
          placeholder="Please enter your message" required
        />
        <ValidationError 
          prefix="Message" 
          field="message"
          errors={state.errors}
        />
        <button type="submit" disabled={state.submitting} data-action='submit'
    onClick={e => onFormSubmit(e)}>
          Submit
        </button>
      </form>
    </div>
    </>
  )
}

export default Contact
