import { React, useEffect, useState } from "react";
import Back from "../common/back/Back" ;
import searchIcon from "../../search.svg";
import "../../App.css";
import { client, urlFor } from "../../client";
import { AiFillStar } from 'react-icons/ai'

const Alevel = () => {
  const [allResources, setAllResources] = useState([]);
  // set search query to empty string
  const [q, setQ] = useState("");
  const [searchParam] = useState(["name", "type"]);
  const [filterParam, setFilterParam] = useState(["All"]);

  useEffect(() => {
  const query = '*[_type == "resource" && syllabus == "Alevel"]';

    client.fetch(query).then((data) => setAllResources(data))
  }, []);

    function search(allResources) {
       return allResources.filter((resource) => {
    /*
    // in here we check if our region is equal to our c state
    // if it's equal to then only return the items that match
    // if not return All the countries
    */
       if (resource.type === filterParam) {
           return searchParam.some((newResource) => {
             return (
               resource[newResource]
                   .toString()
                   .toLowerCase()
                   .indexOf(q.toLowerCase()) > -1
                        );
                    });
                } else if (filterParam == "All") {
                    return searchParam.some((newResource) => {
                        return (
                            resource[newResource]
                                .toString()
                                .toLowerCase()
                                .indexOf(q.toLowerCase()) > -1
                        );
                    });
                }

            });
        }

  return (

    <>

        <Back title="A Level"/>

        <div className="search">
        <input type="search" name="search-form" id="search-form" placeholder="Search for ..." value={q} onChange={(e) => setQ(e.target.value)}/>

        <img 
        src={searchIcon} 
        alt="search"/>
        </div>

        <div className="filter-resource">
            <select
        /*
        // here we create a basic select input
        // we set the value to the selected value
        // and update the setFilterParam() state every time onChange is called
        */
          onChange={(e) => {
          setFilterParam(e.target.value);
          }}
          className="custom-select"
          aria-label="Filter">
            <option value="All">All</option>
            <option value="Past Papers">Past Papers</option>
            <option value="Syllabus & Revision Guide">Syllabus & Revision Guide</option>
            <option value="Textbooks & Revision Notes">Textbooks & Revision Notes</option>
            <option value="Youtube Channel">Youtube Channel</option>
            <option value="A Level Forums">A Level Forums</option>
            </select>
            <span className="focus"></span>
        </div>


      <div className="wrapper">
          {search(allResources).map((resource) => 
              <div className="card">
            <a href={resource.link} target="_blank" rel="noreferrer">
            <div>
                <p><AiFillStar /></p>
            </div>

            <div>
                <img src={urlFor(resource.image && resource.image[0])} alt={resource.name}/>
            </div>
            <div>
                <span>{resource.type}</span>
                <h3>{resource.name}</h3>
            </div>
            </a>
        </div>
         )}
      </div>
    </>

  )

}

var x, i, j, l, ll, selElmnt, a, b, c;
/* Look for any elements with the class "custom-select": */
x = document.getElementsByClassName("custom-select");
l = x.length;
for (i = 0; i < l; i++) {
  selElmnt = x[i].getElementsByTagName("select")[0];
  ll = selElmnt.length;
  /* For each element, create a new DIV that will act as the selected item: */
  a = document.createElement("DIV");
  a.setAttribute("class", "select-selected");
  a.innerHTML = selElmnt.options[selElmnt.selectedIndex].innerHTML;
  x[i].appendChild(a);
  /* For each element, create a new DIV that will contain the option list: */
  b = document.createElement("DIV");
  b.setAttribute("class", "select-items select-hide");
  for (j = 1; j < ll; j++) {
    /* For each option in the original select element,
    create a new DIV that will act as an option item: */
    c = document.createElement("DIV");
    c.innerHTML = selElmnt.options[j].innerHTML;
    c.addEventListener("click", function(e) {
        /* When an item is clicked, update the original select box,
        and the selected item: */
        var y, i, k, s, h, sl, yl;
        s = this.parentNode.parentNode.getElementsByTagName("select")[0];
        sl = s.length;
        h = this.parentNode.previousSibling;
        for (i = 0; i < sl; i++) {
          if (s.options[i].innerHTML == this.innerHTML) {
            s.selectedIndex = i;
            h.innerHTML = this.innerHTML;
            y = this.parentNode.getElementsByClassName("same-as-selected");
            yl = y.length;
            for (k = 0; k < yl; k++) {
              y[k].removeAttribute("class");
            }
            this.setAttribute("class", "same-as-selected");
            break;
          }
        }
        h.click();
    });
    b.appendChild(c);
  }
  x[i].appendChild(b);
  a.addEventListener("click", function(e) {
    /* When the select box is clicked, close any other select boxes,
    and open/close the current select box: */
    e.stopPropagation();
    closeAllSelect(this);
    this.nextSibling.classList.toggle("select-hide");
    this.classList.toggle("select-arrow-active");
  });
}

function closeAllSelect(elmnt) {
  /* A function that will close all select boxes in the document,
  except the current select box: */
  var x, y, i, xl, yl, arrNo = [];
  x = document.getElementsByClassName("select-items");
  y = document.getElementsByClassName("select-selected");
  xl = x.length;
  yl = y.length;
  for (i = 0; i < yl; i++) {
    if (elmnt == y[i]) {
      arrNo.push(i)
    } else {
      y[i].classList.remove("select-arrow-active");
    }
  }
  for (i = 0; i < xl; i++) {
    if (arrNo.indexOf(i)) {
      x[i].classList.add("select-hide");
    }
  }
}

/* If the user clicks anywhere outside the select box,
then close all select boxes: */
document.addEventListener("click", closeAllSelect);


export default Alevel
