import { React, useEffect, useState } from "react";
import Back from "../common/back/Back" ;
import searchIcon from "../../search.svg";
import "../../App.css";
import { client, urlFor } from "../../client";
import { AiFillStar } from 'react-icons/ai'

const IGCSE = () => {
  const [allResources, setAllResources] = useState(null);
  //     set search query to empty string
  const [q, setQ] = useState("");
  const [searchParam] = useState(["name", "type"]);

  useEffect(() => {
    const query = '*[_type == "resource" && syllabus=="IGCSE"]';

    client.fetch(query).then((data) => setAllResources(data));
  }, []);

  function search(allResources) {
        return allResources.filter((resource) => {
            return searchParam.some((newResource) => {
                return (
                    resource[newResource]
                        .toString()
                        .toLowerCase()
                        .indexOf(q.toLowerCase()) > -1
                );
            });
        });
    }

  return (

    <>

        <Back title="IGCSE"/>

        <div className="search">
        <input type="search" name="search-form" id="search-form" placeholder="Search for ..." value={q} onChange={(e) => setQ(e.target.value)}/>

        <img 
        src={searchIcon} 
        alt="search"/>
        </div>
        <div>
        <select
            /*
            // here we create a basic select input
            // we set the value to the selected value
            // and update the setFilterParam() state every time onChange is called
            */
              onChange={(e) => {
              setFilterParam(e.target.value);
              }}
              className="custom-select"
              aria-label="Filter Countries By Region">
                <option value="All">Filter</option>
                <option value="Africa">Past Papers</option>
                <option value="Americas">Syllabus & Revision Guide</option>
                <option value="Asia">Textbooks & Revision Notes</option>
                <option value="Europe">Youtube Channel</option>
                <option value="Oceania">IGCSE Forums</option>
          </select>
                <span className="focus"></span>
          </div>

      <div className="wrapper">
        {console.log(allResources)}

          {search(allResources).map((resource) => 
              <div className="card">
            <a href={resource.link} target="_blank" rel="noreferrer">
            <div>
                <p><AiFillStar /></p>
            </div>

            <div>
                <img src={urlFor(resource.image && resource.image[0])} alt={resource.name}/>
            </div>
            <div>
                <span>{resource.type}</span>
                <h3>{resource.name}</h3>
            </div>
            </a>
        </div>
         )}
      </div>
    </>

  )

}


export default IGCSE
