import { React, useEffect, useState } from "react";
import Back from "../common/back/Back" ;
import searchIcon from "../../search.svg";
import "../../App.css";
import { Link } from "react-router-dom";
import { client } from "../../client";
import { AiFillStar } from 'react-icons/ai'

const IGCSE = () => {
  const [allResources, setAllResources] = useState(null);

  useEffect(() => {
    client
      .fetch(
        `*[_type == "resource"]{
        name,
        slug,
        image{
          asset->{
          _id,
          url
        }
      }
    }`
      )
      .then((data) => setAllResources(data))
      .catch(console.error);
  }, []);

  return (

    <>

        <Back title="IGCSE"/>

        <div className="search">
        <input placeholder="Search for IGCSE resources" value=""/>

        <img 
        src={searchIcon} 
        alt="search"/>
        </div>

      <div className="wrapper">
        {allResources &&
          allResources.map((resource) => 
              <div className="card">
            <Link>
            <div>
                <p><AiFillStar /></p>
            </div>

            <div>
                <img src={resource.image.asset.url}  alt=""/>
            </div>
            <div>
                <span>{resource.type}</span>
                <h3>{resource.name}</h3>
            </div>


            </Link>
        </div>
         )}
      </div>
    </>

  )

}


export default IGCSE
