import React, { useEffect, useState } from "react";
import Back from "../common/back/Back" ;
import searchIcon from "../../search.svg";
import "../../App.css";
import Card from '../../card';
import { Link } from "react-router-dom";
import { client } from "../../client";
import { Resource } from '../../../Resource'


const IGCSE = ({ resources }) => {

  return (
    <>
        <Back title="IGCSE"/>

        <div className="search">
        <input placeholder="Search for IGCSE resources" value=""/>

        <img 
        src={searchIcon} 
        alt="search"/>
        </div>
      <div className="wrapper">
        {resources?.map((resource) => (
              <Resource key={resource._id} resource={resource}/>
         ))}
      </div>
    </>

  )

}



export const getServerSideProps = async () =>{
  const query = '*[_type == "resource"]';
  const resources = await client.fetch(query);


  return{
    props: { resources }
  }

}

export default IGCSE
