import { React, useEffect, useState } from "react";
import Back from "../common/back/Back" ;
import searchIcon from "../../search.svg";
import "../../App.css";
import { client, urlFor } from "../../client";
import { AiFillStar } from 'react-icons/ai'

const IGCSE = () => {
  const [allResources, setAllResources] = useState(null);

  useEffect(() => {
    const query = `*[_type == "resource" ]`;

    client.fetch(query).then((data) => setAllResources(data));
  }, []);

  return (

    <>

        <Back title="IGCSE"/>

        <div className="search">
        <input placeholder="Search for IGCSE resources" value=""/>

        <img 
        src={searchIcon} 
        alt="search"/>
        </div>

      <div className="wrapper">
        {console.log(allResources)}

          {allResources && allResources && allResources.map((resource) => 
              <div className="card">
            <a href={resource.link} target="_blank">
            <div>
                <p><AiFillStar /></p>
            </div>

            <div>
                <img src={urlFor(resource.image && resource.image[0])} alt={resource.name}/>
            </div>
            <div>
                <span>{resource.type}</span>
                <h3>{resource.name}</h3>
            </div>
            </a>
        </div>
         )}
      </div>
    </>

  )

}


export default IGCSE
