import './App.css';

// useState is a react hook
import { useState, useEffect } from 'react';

// creating custom components using dynamic props(properties) 

{/*const Person = (props) => {
  return(
    <>
    <h1>Year: {props.year}</h1>
    <h2>Question : {props.question}</h2>
    </>
  )
}*/}

// state is used to represent a piece of information about a component's current situation
// State uses the concept of array destructuring

const App = () => {
  const name = "Nana Yaw";


  const [counter, setCounter] = useState(0);

  // allows us to do something on some kind of event
  // [] is a dependency array. When empty, the code in the function runs only at the initial load of the component. 
  useEffect(() =>{
    setCounter(100);
  }, []
  );
  // you can call a block of code whenever a variable changes by providing the variable inside the [] 
  return (
    // using a component within another component
    <div className="App">
      {/*<h1>Hello {name}</h1>
      <Person year={2018}/>
      <Person/>
      <Person/>
      <Person/>
      <Person/> */}


      <button onClick={() => setCounter((prevCount) => prevCount - 1)}>-</button>
      <h1>{counter}</h1>
       <button onClick={() => setCounter((nextCount) => nextCount + 1)}>+</button>

    </div>
  );
}

export default App;
