import React from 'react';
import { useEffect } from 'react';
import './App.css';
import searchIcon from './search.svg';
// OMDB API Key
// 6480163c

const API_URL = 'https://www.omdbapi.com?apikey=6480163c';

const movie1 = {
    "Title": "No Time to Die: Official Title Reveal of Bond 25",
    "Year": "2019",
    "imdbID": "tt10839440",
    "Type": "movie",
    "Poster": "https://m.media-amazon.com/images/M/MV5BZjA3ZjhiODUtN2QxYy00MzlhLWFlYzYtZTg1ODQ2YjM0YjY3XkEyXkFqcGdeQXVyMzM3ODE4MzY@._V1_SX300.jpg"
}

const App = () => {
  const searchMovies = async(title) =>{
     const response = await fetch(`${API_URL}&s={title}`);
     const data = await response.json();
     console.log(data.Search);
  }
  useEffect(() => {
    searchMovies('Aquaman');
  }, []);


  return (
    <div className="app">

      <h1>MoxieLand</h1>

      <div className="search">
        <input placeholder="Search for movies" value="Spiderman" onChange={() =>{}}/>

        <img src={searchIcon} alt="search" onClick={() =>{}}/>
      </div>


      <div className="container">
        <div className="movie">
          <div>
            <p>{movie1.Year}</p>
          </div>
          <div>
            <img src={movie1.Poster !== 'N/A' ? movie1.Poster: 'https://via.placeholder.com/400'} alt = {movie1.Title}/>
          </div>

          <div>
            <span>{movie1.Type}</span>
            <h3>{movie1.Title}</h3>
          </div>

        </div>


      </div>


    </div>
  );
}

export default App
